import React from 'react';
import 'tachyons';
import './FaceRecognition.css';
const FaceRecognition = ({ imgUrl, boundingBox }) => {
    // calculate bounding box in pixels
    let img = document.querySelector("#face-image");
    console.log(img, boundingBox);
    let style = {};
    if (img !== null) {
        style = {
            left: boundingBox.left_col * img.width,
            right: (boundingBox.right_col - 1) * img.width,
            top: boundingBox.top_row * img.height,
            bottom: (boundingBox.bottom_row - 1) * img.height
        };

        //console.log(img.width, img.height, style.left, style.right);
    }

    let displayState = imgUrl.length < 1 ? 'none' : 'visible';
    console.log(displayState);
    return (
        <div className="tc mt3" style={{display:displayState}}>
            <img alt='' src={imgUrl} id="face-image"></img>
            <div className="bounding-box" style={style}></div>
        </div>
    )
}

export default FaceRecognition;
