import React from 'react';
import 'tachyons';
import './FaceRecognition.css';
const FaceRecognition = ({ imgUrl, boundingBox }) => {
    // calculate bounding box in pixels
    let img = <img alt='' src={imgUrl} className="faceImage"></img>;

    pixelBoundingBox = {
        left: boundingBox.leftCol * img.width,
        right: (boundingBox.rightCol - 1) * img.width,
        top: boundingBox.topRow * img.height,
        bottom: (boundingBox.bottomRow - 1) * img.height
    };

    return (
        <div className="tc mt3">
            {img}
        </div>
    )
}

export default FaceRecognition;
