import React from 'react';
import 'tachyons';
import './FaceRecognition.css';
const FaceRecognition = ({ imgUrl, boundingBox }) => {
    // calculate bounding box in pixels
    let img = document.querySelector("#face-image");
    console.log(img, boundingBox);
    let style = {};

    if (img !== null && img.width !== undefined) {
        let width = Number(img.width);
        let height = Number(img.height);
        style = {
            left: Math.clamp(boundingBox.left_col * width, 3, width - 3),
            right: Math.clamp((1-boundingBox.right_col) * width, width - 3),
            top: Math.clamp(boundingBox.top_row * height, 3, height - 3),
            bottom: Math.clamp((1-boundingBox.bottom_row) * height, 3, height -3)
        };
        console.log(style);
    }

    return (
        <div className="" style={{display:'flex', justifyContent:'center'}}>
            <div className="tc mt3 absolute">
                <img alt='' src={imgUrl} id="face-image"></img>
                <div className="bounding-box" style={style}></div>
            </div>
        </div>
        
    )
}

export default FaceRecognition;
